// import React from 'react';
import React, {useState}from 'react';
import fetchJsonp from 'fetch-jsonp';

function Home(props) {

const [title,
setTitle]=useState('');
const [orderBy,
setOrderBy]=useState('ALBUM_ASC');
const [musics,
setMusics]=useState([]);

function changeTitle(event) {
setTitle(event.target.value);
}

function changeOrder(event) {
setOrderBy(event.target.value);
}

function changeMusic(event) {
setMusics(event.target.value);
}

function onSearch(event) {
    event.preventDefault(); // EmpÃªche le navigateur de recharger la page
    const encodedTitle=encodeURIComponent(title);
    fetchJsonp(`https: //api.deezer.com/search?q=${encodedTitle}&order=${orderBy}&output=jsonp`
    ) .then(res=> res.json()) .then(data=> data.data) .then(musics=> {
    setMusics(musics);
}

);


}

return (<main className="container mt-3">
    <p>Recherchez un titre sur Deezer en utilisant le formulaire suivant :</p>
    <hr />
    <form onSubmit={ onSearch }>
        <div className="row"> <label htmlFor="searchText" className="col-sm-2 col-form-label text-right">Titre&nbsp;
                :</label>
            <div className="col-sm-4"> <input type="text" className="form-control" id="searchText"
                    placeholder="Eminem, Armin Van Buuren, Rihanna, ..." onChange={ changeTitle } /> </div> <label
                htmlFor="searchText" className="col-sm-2 col-form-label text-right">Trier par :</label>
            <div className="col-sm-2"> <select id="order" className="custom-select" onChange={ changeOrder }>
                    <option value="ALBUM_ASC">Album</option>
                    <option value="ARTIST_ASC">Artiste</option>
                    <option value="TRACK_ASC">Musique</option>
                    <option value="RANKING">Les plus populaires</option>
                    <option value="RATING_ASC">Les mieux notÃ©s</option>
                </select> </div>
            <div className="col-sm-2 text-right"> <input type="submit" className="btn btn-primary" value="Go" /> </div>
        </div>
    </form>
    <hr /> {
    /* <h3>Aucun rÃ©sultat pour cette recherche ...</h3> */
    }

    <h2>RÃ©sultats</h2>
    <div className="card-group search-results"> {
        musics.map(music => {
        return (
        <div>
            {music.title}


            <div className="card w-50" style={{flex: 'initial'}}>
                <div className="card-body text-left">
                    <div className="media mb-2">
                        <img className="align-self-center mr-2 w-25"
                            src="http://via.placeholder.com/64x64png?text=Lose+Yourself" alt="" />
                        <div className="media-body">
                            <h5 className="card-title">Eminem</h5>
                            <h6 className="card-subtitle mb-2 text-muted">Eminem / Curtain Call</h6>
                        </div>
                    </div>
                    <audio src="zik.mp3" className="w-100" controls></audio><br />
                    <a href="#" className="btn btn-sm btn-danger"><i className="fas fa-heart"></i> Ajouter aux
                        favoris</a>
                </div>
            </div>

        </div>
        );
        })}
    </div>
</main>);
}

export default Home;
